import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom'; // Import Link
import { getUserProfileApi } from '../api/Api';

const Profile = () => {
    const [userProfile, setUserProfile] = useState(null);
    const user = JSON.parse(localStorage.getItem("user"));

    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Assuming you have access to the userId
    const userId = user._id

    useEffect(() => {
        const fetchUserProfile = async () => {
            try {
                const response = await getUserProfileApi(userId);
                setUserProfile(response.data.user);
                setLoading(false);
            } catch (error) {
                setError(error.message);
                setLoading(false);
            }
        };

        fetchUserProfile();
    }, [userId]); // Add userId to the dependency array

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div className="container mt-5">
            <div className="row justify-content-center">
                <div className="col-md-6">
                    <div className="card p-4">
                        <h2 className="text-center mb-4">User Profile</h2>
                        {userProfile && (
                            <form>
                                <div className="mb-3">
                                    <label htmlFor="firstName" className="form-label">
                                        First Name
                                    </label>
                                    <input
                                        type="text"
                                        className="form-control mb-3"
                                        value={userProfile.firstName}
                                        disabled
                                    />
                                </div>
                                <div className="mb-3">
                                    <label htmlFor="lastName" className="form-label">
                                        Last Name
                                    </label>
                                    <input
                                        type="text"
                                        className="form-control mb-3"
                                        value={userProfile.lastName}
                                        disabled
                                    />
                                </div>
                                <div className="mb-3">
                                    <label htmlFor="email" className="form-label">
                                        Email
                                    </label>
                                    <input
                                        type="email"
                                        className="form-control mb-3"
                                        value={userProfile.email}
                                        disabled
                                    />
                                </div>
                                <div className="mb-3">
                                    <label htmlFor="isAdmin" className="form-label">
                                        Admin Status
                                    </label>
                                    <input
                                        type="text"
                                        className="form-control mb-3"
                                        value={userProfile.isAdmin ? 'Yes' : 'No'}
                                        disabled
                                    />
                                </div>
                            </form>
                        )}
                        {/* Replace button with Link component */}
                        <Link
                            className="btn btn-dark w-100"
                            to={`/edit/${user._id}`} // Update the link to include userId
                        >
                            Edit Profile
                        </Link>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Profile;
