import React, { useEffect, useState } from "react";
import { getCommentsApi, getSingleBlogApi } from "../api/Api"; // Adjust import path

const SingleBlogPost = ({ blogId }) => {
  const [blog, setBlog] = useState(null);
  const [comments, setComments] = useState([]);

  useEffect(() => {
    const fetchBlog = async () => {
      try {
        const response = await getSingleBlogApi(blogId);
        console.log("Response Data:", response.data); // Log the response data
        setBlog(response.data.blog);
      } catch (error) {
        console.error("Error fetching single blog post:", error);
      }
    };

    const fetchComments = async () => {
      try {
        const response = await getCommentsApi(blogId); // Fetch comments using the getCommentsApi function
        console.log("Comments Data:", response.data); // Log the comments data
        setComments(response.data.comments);
      } catch (error) {
        console.error("Error fetching comments:", error);
      }
    };

    fetchBlog();
    fetchComments();
  }, [blogId]);

  if (!blog) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <div className="media my-4 d-flex align-items-start">
        <img
          src={blog.blogImageUrl || "https://via.placeholder.com/150"}
          className="mr-3"
          alt={blog.title}
          style={{ width: "150px", height: "150px" }}
        />
        <div className="media-body flex-grow-1" style={{ marginLeft: "1rem" }}>
          <h5 className="mt-0">{blog.title}</h5>
          <p className="small text-muted mb-0">
            <strong>Author:</strong>{" "}
            {`${blog.user?.firstName || "Unknown"} ${
              blog.user?.lastName || "Unknown"
            }`}
          </p>
          <p>{blog.content}</p>
        </div>
      </div>
      <h6>Comments:</h6>
      {comments.map((comment, index) => (
        <div key={index}>
          <p>{comment.content}</p>
          <p>
            Author:{" "}
            {`${comment.user?.firstName || "Unknown"} ${
              comment.user?.lastName || "Unknown"
            }`}
          </p>
        </div>
      ))}
    </div>
  );
};

export default SingleBlogPost;
