import React, { useState } from 'react';
import { requestCodeApi } from '../api/Api';

const ForgotPassword = () => {
    const [email, setEmail] = useState('');
    const [isLoading, setIsLoading] = useState(false);

    const handleRequestCode = async () => {
        setIsLoading(true);
        try {
            await requestCodeApi(email);
            console.log("Code requested successfully");
            // Handle success - Display a message or navigate to another page
        } catch (error) {
            // Handle error - Display error message to the user
            console.error("Error requesting code:", error.message);
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div>
            <h2>Forgot Password</h2>
            <input
                type="email"
                placeholder="Enter your email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
            />
            <button onClick={handleRequestCode} disabled={isLoading}>
                {isLoading ? 'Sending Code...' : 'Send Code'}
            </button>
        </div>
    );
};

export default ForgotPassword;
