import React, { useState } from "react";
import { Navigate } from "react-router-dom";
import { toast } from "react-toastify";
import { changePasswordApi } from "../api/Api";

const ChangePassword = () => {
    const [oldPassword, setOldPassword] = useState("");
    const [newPassword, setNewPassword] = useState("");
    const [error, setError] = useState(null);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Call the changePasswordApi function with old and new passwords
            await changePasswordApi(oldPassword, newPassword);
            // Clear form fields and reset error state on successful password change
            setOldPassword("");
            setNewPassword("");

            toast.success("Password changed successfully");
            Navigate("/"); // Navigate to the homepage
            setError(null);
        } catch (error) {
            // Handle error if password change fails
            if (error && error.response && error.response.data) {
                // If error object, response property, and data property exist
                toast.error(error.response.data.message);
            } else {
                // Otherwise, display a generic error message
                toast.error("An error occurred. Please try again.");
            }
            setError(error); // Set the error state to the error object
        }
    };

    return (
        <div className="container mt-5">
            <div className="row justify-content-center">
                <div className="col-md-6">
                    <div className="card p-4">
                        <h2 className="text-center mb-4">Change Password</h2>
                        <form onSubmit={handleSubmit}>
                            <div className="mb-3">
                                <label htmlFor="oldPassword" className="form-label">
                                    Old Password
                                </label>
                                <input
                                    value={oldPassword}
                                    onChange={(e) => setOldPassword(e.target.value)}
                                    type="password"
                                    className="form-control mb-3"
                                    placeholder="Enter your old password"
                                />
                            </div>
                            <div className="mb-3">
                                <label htmlFor="newPassword" className="form-label">
                                    New Password
                                </label>
                                <input
                                    value={newPassword}
                                    onChange={(e) => setNewPassword(e.target.value)}
                                    type="password"
                                    className="form-control mb-3"
                                    placeholder="Enter your new password"
                                />
                            </div>
                            {error && <div className="text-danger mb-3">{error}</div>}
                            <button type="submit" className="btn btn-dark w-100">
                                Change Password
                            </button>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ChangePassword;
