import React from 'react';
import { updateUserRoleApi } from '../api/Api';
import '..//.css'; // Import custom CSS styles

const UserTable = ({ users, isAdmin, updateUserList }) => {
    const handleRoleChange = (userId) => {
        // Implement logic to change user role
        updateUserRoleApi(userId)
            .then(response => {
                // Handle success response
                console.log('User role updated successfully:', response.data);
                // Update user list in parent component
                updateUserList();
            })
            .catch(error => {
                // Handle error response
                console.error('Error updating user role:', error);
            });
    };

    return (
        <table className="table table-bordered table-striped mt-4 mx-auto">
            <thead className="table-dark">
                <tr>
                    <th>First Name</th>
                    <th>Last Name</th>
                    <th>Email</th>
                    <th>Admin</th>
                    <th>Action</th>
                </tr>
            </thead>
            <tbody>
                {users.map(user => (
                    <tr key={user._id}>
                        <td>{user.firstName}</td>
                        <td>{user.lastName}</td>
                        <td>{user.email}</td>
                        <td>{user.isAdmin ? 'Yes' : 'No'}</td>
                        <td>
                            <label className="toggle">
                                <input type="checkbox" checked={user.isAdmin} onChange={() => handleRoleChange(user._id)} />
                                <span className="knob"></span>
                            </label>
                        </td>
                    </tr>
                ))}
            </tbody>
        </table>
    );
};

export default UserTable;
