// BlogPost.js
import React from "react";

const BlogPost = ({ post }) => {
    // Hardcoded image URL for now
    const defaultImageUrl = "https://example.com/default-image.jpg";
    const imageUrl = post.image || defaultImageUrl;

    return (
        <div className="blog-post">
            {imageUrl && (
                <img
                    src={imageUrl}
                    alt={post.title}
                    style={{ width: "200px", height: "200px" }}
                />
            )}
            <h2>{post.title}</h2>
            <p>{post.content}</p>
        </div>
    );
};

export default BlogPost;
