// ViewBlog.jsx
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom'; // Import useParams to access URL params
import SingleBlogPost from '../components/SingleBlogPost';
import { loginUserApi } from '../api/api'; // Import the updated loginUserApi function

const ViewBlog = () => {
    const { blogId } = useParams(); // Use useParams to get the blogId from URL params
    const [loggedInUser, setLoggedInUser] = useState(null); // State to store logged-in user information

    useEffect(() => {
        const fetchData = async () => {
            try {
                const { user } = await loginUserApi(); // Call loginUserApi to retrieve user information
                setLoggedInUser(user); // Set the logged-in user in state
            } catch (error) {
                console.error('Error fetching user data:', error);
            }
        };

        fetchData();
    }, []);

    return (
        <div className="container mt-4">
            <div className="row">
                <div className="col-md-9">
                    <SingleBlogPost blogId={blogId} loggedInUserId={loggedInUser ? loggedInUser._id : null} /> {/* Pass loggedInUserId as a prop */}
                </div>
            </div>
        </div>
    );
};

export default ViewBlog;
