// src/components/BlogPosts.js
import React, { useEffect, useState } from 'react';
import { getAllBlogsApi } from '../api/Api'; // Adjust the import path

const BlogPosts = () => {
    const [blogs, setBlogs] = useState([]);

    useEffect(() => {
        const fetchBlogs = async () => {
            try {
                const response = await getAllBlogsApi();
                const responseData = response.data;

                if (responseData && responseData.blogs && Array.isArray(responseData.blogs)) {
                    setBlogs(responseData.blogs);
                } else {
                    console.error('Invalid response data structure:', responseData);
                }
            } catch (error) {
                console.error('Error fetching blogs:', error);
            }
        };

        fetchBlogs();
    }, []); // Empty dependency array ensures the effect runs only once on mount

    const getBlogImage = (blog) => {
        return blog.imageUrl || 'https://via.placeholder.com/150'; // Use placeholder image if no imageUrl is provided
    };

    return (
        <div>
            <h2>Blog Posts</h2>
            {blogs.map((blog) => (
                <div key={blog.id} className="media my-4 d-flex align-items-start">
                    <img
                        src={getBlogImage(blog)}
                        className="mr-3"
                        alt={blog.title}
                        style={{ width: '150px', height: '150px' }}
                    />
                    <div className="media-body flex-grow-1" style={{ marginLeft: '1rem' }}>
                        <h5 className="mt-0">{blog.title}</h5>
                        <p className="small text-muted mb-0">
                            <strong>Author:</strong> {`${blog.firstName} ${blog.lastName}`}
                        </p>
                        <p>{blog.content}</p>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default BlogPosts;
