import React, { useEffect, useState } from 'react';
import { Link, useParams } from 'react-router-dom';
import { paginationBlogApi } from '../api/Api';

const BlogPosts = () => {
    const [blogs, setBlogs] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const { pageNumber } = useParams(); // Get the pageNumber from URL params

    useEffect(() => {
        const fetchBlogs = async () => {
            try {
                // Fetch blogs for the specified page number
                const response = await paginationBlogApi(pageNumber || currentPage);
                const responseData = response.data;

                if (responseData && responseData.blogs && Array.isArray(responseData.blogs)) {
                    setBlogs(responseData.blogs);
                } else {
                    console.error('Invalid response data structure:', responseData);
                }
            } catch (error) {
                console.error('Error fetching blogs:', error);
            }
        };

        fetchBlogs();
    }, [pageNumber, currentPage]); // Execute useEffect whenever pageNumber or currentPage changes

    const handlePageChange = (page) => {
        setCurrentPage(page);
    };

    const renderPaginationButtons = () => {
        const buttons = [];
        for (let i = 1; i <= 5; i++) { // Change 5 to the total number of pages or a dynamic value based on API response
            buttons.push(
                <button key={i} onClick={() => handlePageChange(i)}>{i}</button>
            );
        }
        return buttons;
    };

    return (
        <div>
            <h2>Blog Posts</h2>
            {blogs.map((blog) => (
                <Link to={`/blogs/${blog._id}`} key={blog._id} className="text-decoration-none text-dark">
                    <div className="media my-4 d-flex align-items-start">
                        <img
                            src={getBlogImage(blog)}
                            className="mr-3"
                            alt={blog.title}
                            style={{ width: '150px', height: '150px' }}
                        />
                        <div className="media-body flex-grow-1" style={{ marginLeft: '1rem' }}>
                            <h5 className="mt-0">{blog.title}</h5>
                            <p className="small text-muted mb-0">
                                <strong>Author:</strong>{' '}
                                {`${blog.user.firstName || 'Unknown'} ${blog.user.lastName || 'Unknown'}`}
                            </p>
                            <p>{truncateContent(blog.content, 300)}</p>
                        </div>
                    </div>
                </Link>
            ))}
            <div>
                {renderPaginationButtons()}
            </div>
        </div>
    );
};

export default BlogPosts;
