// src/components/ViewBlog.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom'; // Import useParams to access URL params
import SingleBlogPost from '../components/SingleBlogPost';
import { getLoggedInUserId } from '../api/Auth'; // Import a function to get the logged-in user ID

const ViewBlog = () => {
    const { blogId } = useParams(); // Use useParams to get the blogId from URL params
    const [loggedInUserId, setLoggedInUserId] = useState(null); // State to store the logged-in user ID

    useEffect(() => {
        // Fetch the logged-in user ID when the component mounts
        const fetchLoggedInUserId = async () => {
            try {
                const userId = await getLoggedInUserId(); // Call your function to get the logged-in user ID
                setLoggedInUserId(userId);
            } catch (error) {
                console.error('Error fetching logged-in user ID:', error);
            }
        };

        fetchLoggedInUserId(); // Call the function to fetch the logged-in user ID
    }, []);

    return (
        <div className="container mt-4">
            <div className="row">
                <div className="col-md-9">
                    <SingleBlogPost blogId={blogId} loggedInUserId={loggedInUserId} /> {/* Pass the loggedInUserId prop */}
                </div>
            </div>
        </div>
    );
};

export default ViewBlog;
