import axios from "axios";

const Api = axios.create({
  baseURL: "http://localhost:5501",
  withCredentials: true,
  headers: {
    "Content-Type": "multipart/form-data",
  },
});

export const testApi = () => Api.get("/test");

export const createUserApi = (data) => Api.post("/api/user/create", data);

// In loginUserApi function in api.js
export const loginUserApi = async (data) => {
  try {
    const response = await Api.post("/api/user/login", data);
    const { token } = response.data; // Assuming the token is returned in the response
    localStorage.setItem("token", token); // Store the token in localStorage
    console.log("Token stored in localStorage:", token); // Log the token value
    return response;
  } catch (error) {
    throw error;
  }
};

export const createBlogApi = (data, config) =>
  Api.post("/api/blog/create-blog", data, config);

export const getAllBlogsApi = () => Api.get("/api/blog/get-blogs");

export const logoutUser = () => {
  localStorage.removeItem("token"); // Clear the token from localStorage
};

export const getAuthorizationHeader = () => ({
  headers: {
    authorization: `Bearer ${localStorage.getItem("token")}`,
  },
});


export const getSingleBlogApi = () => Api.get("/api/blog/get-blogs");
