// AdminDashboard.js
import React, { useEffect, useState } from 'react';
import { getAllUsersApi } from '../../api/Api';
import UserTable from '../../components/UserTable';

const AdminDashboard = () => {
    const [users, setUsers] = useState([]);

    useEffect(() => {
        getAllUsersApi()
            .then(response => {
                setUsers(response.data);
            })
            .catch(error => {
                console.error('Error fetching users:', error);
            });
    }, []);

    // Assuming the logged-in user's information is stored in localStorage
    const loggedInUser = JSON.parse(localStorage.getItem('user'));
    const isAdmin = loggedInUser && loggedInUser.isAdmin; // Check if the logged-in user is an admin

    return (
        <div>
            <h1>You are an admin</h1>
            <h2>User List</h2>
            <UserTable users={users} isAdmin={isAdmin} /> {/* Pass isAdmin prop to UserTable */}
        </div>
    );
};

export default AdminDashboard;
