import React, { useState } from "react";
import { changePasswordApi } from "./api"; // Import the changePasswordApi function

const ChangePasswordForm = () => {
  const [oldPassword, setOldPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Call the changePasswordApi function with old and new passwords
      await changePasswordApi(oldPassword, newPassword);
      // Clear form fields and reset error state on successful password change
      setOldPassword("");
      setNewPassword("");
      setError(null);
    } catch (error) {
      // Handle error if password change fails
      setError(error.response.data.message);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="oldPassword">Old Password:</label>
        <input
          type="password"
          id="oldPassword"
          value={oldPassword}
          onChange={(e) => setOldPassword(e.target.value)}
        />
      </div>
      <div>
        <label htmlFor="newPassword">New Password:</label>
        <input
          type="password"
          id="newPassword"
          value={newPassword}
          onChange={(e) => setNewPassword(e.target.value)}
        />
      </div>
      {error && <div style={{ color: "red" }}>{error}</div>}
      <button type="submit">Change Password</button>
    </form>
  );
};

export default ChangePasswordForm;
