// Sidebar.js
import React from "react";

const Sidebar = ({ categories }) => (
    <div className="sidebar-container">
        <select>
            {categories.map((category, index) => (
                <option key={index} value={category}>
                    {category}
                </option>
            ))}
        </select>
    </div>
);

export default Sidebar;
