import React, { useState } from "react";

const Register = () => {
  // useState (setting input value)
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  //function to change input value
  const changeFirstName = (e) => {
    setFirstName(e.target.value);
  };
  const changeLastName = (e) => {
    setLastName(e.target.value);
  }
  const changeEmail = (e) => {
    setEmail(e.target.value);
  }
  const changePassword = (e) => {
    setPassword(e.target.value);
  }

  //function for button 
  const handleSubmit = (e) => {
    //check is the input value is available
    console.log(firstName, lastName, email, password);
  };
  return (
    <>
      <h1 className="m-4">Create Your Account</h1>
      <form className="m-4 w-25">
        <label>First Name</label>
        <input
          onChange={changeFirstName}
          type="text"
          className="form-control mb-3"
          placeholder="Enter your first name"
        />
        <label>Last Name</label>
        <input
          onChange={changeLastName}
          type="text"
          className="form-control mb-3"
          placeholder="Enter your last name"
        />
        <label>Email</label>
        <input
          onChange={changeEmail}
          type="text"
          className="form-control mb-3"
          placeholder="Enter your email"
        />
        <label>Password</label>
        <input
          onChange={changePassword}
          type="password"
          className="form-control mb-3"
          placeholder="Enter your password"
        />
        <button  className="btn btn-danger w-100">Create an Account</button>
        <a href="" className="text-black text-decoration-none">Already have an account? Login</a>
      </form>
    </>
  );
};

export default Register;
