// src/components/ViewBlog.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import SingleBlogPost from '../components/SingleBlogPost';
import { loginUserApi } from '../api/Api';

const ViewBlog = () => {
    const { blogId } = useParams();
    const [loggedInUserId, setLoggedInUserId] = useState(null); // Initialize loggedInUserId state

    useEffect(() => {
        // Fetch the logged-in user data and extract the user ID
        const fetchLoggedInUser = async () => {
            try {
                const userResponse = await loginUserApi(); // Adjust loginUserApi to fetch logged-in user data
                setLoggedInUserId(userResponse.data._id); // Extract user ID and set it in state
            } catch (error) {
                console.error('Error fetching logged-in user data:', error);
            }
        };

        fetchLoggedInUser();
    }, []);

    return (
        <div className="container mt-4">
            <div className="row">
                <div className="col-md-9">
                    <SingleBlogPost blogId={blogId} loggedInUserId={loggedInUserId} /> {/* Pass loggedInUserId to SingleBlogPost */}
                </div>
            </div>
        </div>
    );
};

export default ViewBlog;
