import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { searchBlogByTitleApi } from "./Api"; // Import the searchBlogByTitleApi function

const Navbar = () => {
  const user = JSON.parse(localStorage.getItem("user"));
  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  const handleLogout = (e) => {
    e.preventDefault();
    localStorage.clear();
    navigate("/login");
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    try {
      // Make a request to search for blogs with the provided search query
      const response = await searchBlogByTitleApi(searchQuery);
      setSearchResults(response.blogs); // Update search results state with the fetched blogs
    } catch (error) {
      console.error("Error searching blogs:", error);
    }
  };

  return (
    <>
      <nav
        className="navbar navbar-expand-lg navbar-light bg-white border-bottom"
        style={{
          borderBottom: "1px solid rgba(0, 0, 0, 0.1)",
          padding: "18px 70px",
        }}
      >
        <div className="container-fluid">
          <div className="d-flex align-items-center">
            <Link className="navbar-brand" to="/">
              <img
                src="assets/images/logo2.png"
                alt="Logo"
                width="40"
                height="40"
                className="d-inline-block align-top"
                style={{ maxHeight: "50px", maxWidth: "100%" }}
              />
            </Link>
          </div>

          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarSupportedContent"
            aria-controls="navbarSupportedContent"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>

          <div className="collapse navbar-collapse" id="navbarSupportedContent">
            <form className="ms-auto" role="search">
              <div className="input-group">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Search blogs"
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                />
                <button
                  className="btn btn-outline-secondary"
                  type="button"
                  onClick={handleSearch}
                >
                  Search
                </button>
              </div>
              {/* Display search results */}
              {searchResults.length > 0 && (
                <div className="mt-2">
                  {searchResults.map((blog) => (
                    <div key={blog._id}>
                      <h5>{blog.title}</h5>
                      <p>{blog.content}</p>
                    </div>
                  ))}
                </div>
              )}
              {/* End of search results */}
            </form>
          </div>
        </div>
      </nav>
    </>
  );
};

export default Navbar;
