import React, { useState } from "react";
import { loginUserApi } from "../api/Api";
import { toast } from "react-toastify";


const Login = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const handleSubmit = (e) => {
        e.preventDefault()
        console.log(email, password)

        const data = {
            email: email,
            password: password
        }



        // //make api call
        // const response = loginUserApi(data)
        // if(response.data.success == false){
        //     toast.error(response.data.message)
        // } else if (response.data.success == true){
        //     toast.success(response.data.message)
        // } else {
        //     toast.error("Server Error")
        // }

        loginUserApi(data).then((res) => {
            if(res.data.success == false){
                toast.error(res.data.message)
            } else{
                toast.success(res.data.message)
                localStorage.setItem("token", res.data.token)

                //set user data
                const jsonDecode = JSON.stringify(res.data.userData)
                localStorage.setItem("user", jsonDecode)
            }
        }).catch((err) => {
            toast.error("Server Error")
            console.log(err.message)
        })
    }
    return (
        <>
            <h1 className="m-4">Login To Your Account</h1>
            <form className="m-4 w-25">

                <label>Email</label>
                <input
                    onChange={(e) => setEmail(e.target.value)}
                    type="email"
                    className="form-control mb-3"
                    placeholder="Enter your email"
                />
                <label>Password</label>
                <input
                    onChange={(e) => setPassword(e.target.value)}
                    type="password"
                    className="form-control mb-3"
                    placeholder="Enter your password"
                />
                <button onClick={handleSubmit} className="btn btn-danger w-100">Login</button>
                <a href="" className="text-black text-decoration-none">Dont have an account? Signup</a>
            </form>
        </>
    );
};

export default Login;
