import React, { useState } from 'react';
import { requestCodeApi } from '../api/Api';

const ForgotPassword = () => {
    const [email, setEmail] = useState('');
    const [isLoading, setIsLoading] = useState(false);

    const handleRequestCode = async () => {
        setIsLoading(true);
        try {
            await requestCodeApi(email);
            console.log("Code requested successfully");
            // Handle success - Display a message or navigate to another page
        } catch (error) {
            // Handle error - Display error message to the user
            console.error("Error requesting code:", error.message);
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="login-container">
            <h2>Forgot Password</h2>
            <form>
                <div className="form-group">
                    <label htmlFor="email">Email</label>
                    <input
                        type="email"
                        id="email"
                        placeholder="Enter your email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                </div>
                <button
                    className="btn btn-primary"
                    onClick={handleRequestCode}
                    disabled={isLoading}
                >
                    {isLoading ? 'Sending Code...' : 'Send Code'}
                </button>
            </form>
        </div>
    );
};

export default ForgotPassword;
