// BlogPost.js
import React from "react";

const BlogPost = ({ post }) => {
  // Hardcoded image URL for now
  const defaultImageUrl = "https://example.com/default-image.jpghttps://th.bing.com/th/id/OIP.wMzAuHDcivlTPKsZEJtA3QHaEo?rs=1&pid=ImgDetMain";
  const imageUrl = post.image || defaultImageUrl;

  return (
    <div className="blog-post">
      <img src={imageUrl} alt={post.title} />
      <h2>{post.title}</h2>
      <p>{post.content}</p>
    </div>
  );
};

export default BlogPost;
