import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { getAllBlogsApi } from '../api/Api';

const BlogPosts = () => {
    const [blogs, setBlogs] = useState([]);

    useEffect(() => {
        const fetchBlogs = async () => {
            try {
                const response = await getAllBlogsApi();
                const responseData = response.data;

                if (responseData && responseData.blogs && Array.isArray(responseData.blogs)) {
                    setBlogs(responseData.blogs);
                } else {
                    console.error('Invalid response data structure:', responseData);
                }
            } catch (error) {
                console.error('Error fetching blogs:', error);
            }
        };

        fetchBlogs();
    }, []);

    const getBlogImage = (blog) => {
        return blog.blogImageUrl || 'https://via.placeholder.com/150';
    };

    return (
        <div>
            <h2>Blog Posts</h2>
            {blogs.map((blog) => (
                <div key={blog._id} className="media my-4">
                    <img
                        src={getBlogImage(blog)}
                        className="mr-3"
                        alt={blog.title}
                        style={{ width: '150px', height: '150px' }}
                    />
                    <div className="media-body">
                        <Link to={`/blogs/${blog._id}`} style={{ textDecoration: 'none', color: 'inherit' }}>
                            <h5 className="mt-0">{blog.title}</h5>
                        </Link>
                        <p className="small text-muted mb-0">
                            <strong>Author:</strong>{' '}
                            {`${blog.user.firstName || 'Unknown'} ${blog.user.lastName || 'Unknown'}`}
                        </p>
                        <p>{blog.content}</p>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default BlogPosts;
