import moment from 'moment';
import React, { useEffect, useState } from 'react';
import { getCommentsApi, getSingleBlogApi } from '../api/Api'; // Adjust import path

const SingleBlogPost = ({ blogId }) => {
    const [blog, setBlog] = useState(null);
    const [comments, setComments] = useState([]);

    useEffect(() => {
        const fetchBlog = async () => {
            try {
                const response = await getSingleBlogApi(blogId);
                console.log('Response Data:', response.data); // Log the response data
                setBlog(response.data.blog);
            } catch (error) {
                console.error('Error fetching single blog post:', error);
            }
        };

        const fetchComments = async () => {
            try {
                const response = await getCommentsApi(blogId); // Fetch comments using the getCommentsApi function
                console.log('Comments Data:', response.data); // Log the comments data
                setComments(response.data.comments);
            } catch (error) {
                console.error('Error fetching comments:', error);
            }
        };

        fetchBlog();
        fetchComments();
    }, [blogId]);

    if (!blog) {
        return <div>Loading...</div>;
    }

    return (
        <div className="container">
            <div className="row">
                <div className="col-md-10 offset-md-1">
                    <h2>{blog.title}</h2>
                    <p className="small text-muted mb-2">
                        <strong>Author:</strong>{' '}
                        <span className="text-grey">{`${blog.user?.firstName || 'Unknown'} ${blog.user?.lastName || 'Unknown'}`}</span>
                    </p>
                    <p className="small text-muted mb-4">
                        <strong>Date:</strong>{' '}
                        <span className="text-grey">{moment(blog.createdAt).format('MMMM Do YYYY, h:mm a')}</span>
                    </p>
                </div>
            </div>
            <div className="row">
                <div className="col-md-10 offset-md-1">
                    <img
                        src={blog.blogImageUrl || 'https://via.placeholder.com/600x400'}
                        alt={blog.title}
                        className="img-fluid mb-4"
                    />
                    <p style={{ whiteSpace: 'pre-wrap' }}>{blog.content}</p>
                </div>
            </div>
            <div className="row">
                <div className="col-md-10 offset-md-1">
                    <h4 className="mt-5">Comments:</h4>
                    {comments.map((comment, index) => (
                        <div key={index} className="mb-3">
                            <p>{comment.content}</p>
                            <p className="small text-muted">
                                <strong>Author:</strong>{' '}
                                <span className="text-grey">{`${comment.user?.firstName || 'Unknown'} ${comment.user?.lastName || 'Unknown'}`}</span>
                            </p>
                            <hr />
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default SingleBlogPost;
