import React, { useEffect, useState } from 'react';
import { Link, useParams } from 'react-router-dom';
import { paginationBlogApi } from '../api/Api';

const BlogPosts = () => {
    const [blogs, setBlogs] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [maxPage, setMaxPage] = useState(1);
    const { pageNumber } = useParams();
    const [searchQuery, setSearchQuery] = useState('');

    useEffect(() => {
        const fetchBlogs = async () => {
            try {
                const response = await paginationBlogApi(pageNumber || currentPage);
                if (response && response.success && Array.isArray(response.blogs)) {
                    setBlogs(response.blogs);
                    setMaxPage(response.maxPage);
                } else {
                    console.error('Invalid response data structure:', response);
                }
            } catch (error) {
                console.error('Error fetching blogs:', error);
            }
        };

        fetchBlogs();
    }, [pageNumber, currentPage]);

    const handleSearch = () => {
        setCurrentPage(1); // Reset current page when performing a new search
        // Implement search logic here
        // Filter the blogs based on the searchQuery
        const filteredBlogs = blogs.filter(blog =>
            blog.title.toLowerCase().includes(searchQuery.toLowerCase())
        );
        return filteredBlogs;
    };

    const filteredBlogs = searchQuery ? handleSearch() : blogs;

    const startIndex = (currentPage - 1) * 10;
    const endIndex = startIndex + 10;

    return (
        <div className="container mt-4">
            <h2 className="mb-4">Blog Posts</h2>
            <input
                type="text"
                placeholder="Search by title"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
            />
            {filteredBlogs.slice(startIndex, endIndex).length === 0 && <p>No blogs found.</p>}
            {filteredBlogs.slice(startIndex, endIndex).map((blog) => (
                <Link to={`/blogs/${blog._id}`} key={blog._id} className="text-decoration-none text-dark">
                    <div className="media my-4 d-flex align-items-start">
                        <img
                            src={blog.blogImageUrl || 'https://via.placeholder.com/150'}
                            className="mr-3"
                            alt={blog.title}
                            style={{ width: '150px', height: '150px', marginRight: '20px' }}
                        />
                        <div className="media-body">
                            <h5 className="mt-0">{blog.title}</h5>
                            <p className="small text-muted">
                                <strong>Author:</strong>{' '}
                                {blog.user ? `${blog.user.firstName || 'Unknown'} ${blog.user.lastName || 'Unknown'}` : 'Unknown'}
                            </p>
                            <p>{blog.content.substring(0, 300)}</p>
                        </div>
                    </div>
                </Link>
            ))}
            <div className="d-flex justify-content-between align-items-center">
                <button
                    className="btn btn-dark"
                    onClick={() => setCurrentPage(currentPage - 1)}
                    disabled={currentPage === 1}
                >
                    Previous
                </button>
                <span>Page {currentPage}</span>
                {Array.from({ length: maxPage }, (_, i) => i + 1).map((page) => (
                    <button
                        key={page}
                        className="btn btn-dark"
                        onClick={() => setCurrentPage(page)}
                        disabled={currentPage === page}
                    >
                        {page}
                    </button>
                ))}
                <button
                    className="btn btn-dark"
                    onClick={() => setCurrentPage(currentPage + 1)}
                    disabled={currentPage === maxPage || filteredBlogs.length === 0}
                >
                    Next
                </button>
            </div>
        </div>
    );
};

export default BlogPosts;
