import moment from 'moment'; // Import moment library for date formatting
import React, { useEffect, useState } from 'react';
import { getCommentsApi, getSingleBlogApi } from '../api/Api'; // Adjust import path

const SingleBlogPost = ({ blogId }) => {
    const [blog, setBlog] = useState(null);
    const [comments, setComments] = useState([]);

    useEffect(() => {
        const fetchBlog = async () => {
            try {
                const response = await getSingleBlogApi(blogId);
                console.log('Response Data:', response.data); // Log the response data
                setBlog(response.data.blog);
            } catch (error) {
                console.error('Error fetching single blog post:', error);
            }
        };

        const fetchComments = async () => {
            try {
                const response = await getCommentsApi(blogId); // Fetch comments using the getCommentsApi function
                console.log('Comments Data:', response.data); // Log the comments data
                setComments(response.data.comments);
            } catch (error) {
                console.error('Error fetching comments:', error);
            }
        };

        fetchBlog();
        fetchComments();
    }, [blogId]);

    if (!blog) {
        return <div>Loading...</div>;
    }

    return (
        <div className="container mt-4">
            <div className="row">
                <div className="col-lg-8 offset-lg-2">
                    <h1 className="mt-4 mb-3">{blog.title}</h1>
                    <p className="lead">
                        by {`${blog.user?.firstName || 'Unknown'} ${blog.user?.lastName || 'Unknown'}`}
                    </p>
                    <hr />
                    {/* <p>Posted on {moment(blog.createdAt).format('MMMM Do, YYYY')}</p> */}
                    <hr />
                    <img
                        className="img-fluid rounded"
                        src={blog.blogImageUrl || 'https://via.placeholder.com/900x300'}
                        alt={blog.title}
                    />
                    <hr />
                    <p className="lead">{blog.content}</p>
                    <hr />
                    <h4 className="mb-3">Comments:</h4>
                    {comments.map((comment, index) => (
                        <div key={index} className="media mb-4">
                            <img
                                className="d-flex mr-3 rounded-circle"
                                src="http://placehold.it/50x50"
                                alt=""
                            />
                            <div className="media-body">
                                <h5 className="mt-0">{comment.user?.firstName || 'Unknown'} {comment.user?.lastName || 'Unknown'}</h5>
                                {comment.content}
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default SingleBlogPost;
