import React, { useState, useEffect } from 'react';
import moment from 'moment';
import { getCommentsApi, getSingleBlogApi } from '../api/Api';
import CommentSection from './CommentSection'; // Import the CommentSection component

const SingleBlogPost = ({ blogId }) => {
    const [blog, setBlog] = useState(null);
    const [comments, setComments] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const blogResponse = await getSingleBlogApi(blogId);
                console.log('Blog Data:', blogResponse.data);
                setBlog(blogResponse.data.blog);

                const commentsResponse = await getCommentsApi(blogId);
                console.log('Comments Data:', commentsResponse.data);
                setComments(commentsResponse.data.comments);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData();
    }, [blogId]);

    if (!blog) {
        return <div>Loading...</div>;
    }

    return (
        <div className="container">
            <div className="row">
                <div className="col-md-10 offset-md-1">
                    <h2>{blog.title}</h2>
                    <p className="small text-muted mb-2">
                        <strong>Author:</strong>{' '}
                        <span className="text-grey">{`${blog.user.firstName || 'Unknown'} ${blog.user.lastName || 'Unknown'}`}</span>
                    </p>
                    <p className="small text-muted mb-4">
                        <strong>Date:</strong>{' '}
                        <span className="text-grey">{moment(blog.createdAt).format('MMMM Do YYYY, h:mm a')}</span>
                    </p>
                </div>
            </div>
            <div className="row">
                <div className="col-md-10 offset-md-1">
                    <img
                        src={blog.blogImageUrl || 'https://via.placeholder.com/600x400'}
                        alt={blog.title}
                        className="img-fluid mb-4"
                    />
                    <p style={{ whiteSpace: 'pre-wrap' }}>{blog.content}</p>
                    {/* Render the CommentSection component */}
                    <CommentSection blogId={blogId} comments={comments} setComments={setComments} />
                </div>
            </div>
        </div>
    );
};

export default SingleBlogPost;
