// BlogPost.js
import React from "react";

const BlogPost = ({ post }) => {
    // Valid placeholder image URL
    const defaultImageUrl = "https://via.placeholder.com/200"; // Replace this with a valid image URL
    const imageUrl = post.imageUrl || defaultImageUrl;

    console.log("Image URL:", imageUrl);

    return (
        <div className="blog-post">
            {imageUrl && (
                <img
                    src={imageUrl}
                    alt={post.title}
                    style={{ width: "200px", height: "200px", marginRight: "20px" }}
                />
            )}
            <div className="blog-post-content">
                <h2>{post.title}</h2>
                <p>{post.content}</p>
            </div>
        </div>
    );
};

export default BlogPost;
