import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";

const Editor = () => {
    const navigate = useNavigate();
    const [title, setTitle] = useState("");
    const [content, setContent] = useState("");
    const [image, setImage] = useState("");

    const handleSubmit = (e) => {
        e.preventDefault();

        // Simulate a successful submission for UI testing
        toast.success("Blog post created successfully!");

        // Redirect to homepage or view the created blog post
        navigate("/");
    };

    // Simple authentication check
    const isAuthenticated = localStorage.getItem("token");

    // Redirect to login if not authenticated
    if (!isAuthenticated) {
        navigate("/login");
        return null; // Render nothing if not authenticated
    }

    return (
        <div className="container mt-5">
            <div className="row justify-content-center">
                <div className="col-md-6">
                    <div className="card p-4">
                        <h2 className="text-center mb-4">Create a Blog Post</h2>
                        <form>
                            <div className="mb-3">
                                <label htmlFor="title" className="form-label">
                                    Title
                                </label>
                                <input
                                    onChange={(e) => setTitle(e.target.value)}
                                    type="text"
                                    className="form-control mb-3"
                                    placeholder="Enter the title of your blog post"
                                />
                            </div>
                            <div className="mb-3">
                                <label htmlFor="content" className="form-label">
                                    Content
                                </label>
                                <textarea
                                    onChange={(e) => setContent(e.target.value)}
                                    className="form-control mb-3"
                                    placeholder="Enter the content of your blog post"
                                />
                            </div>
                            <div className="mb-3">
                                <label htmlFor="image" className="form-label">
                                    Image URL
                                </label>
                                <input
                                    onChange={(e) => setImage(e.target.value)}
                                    type="text"
                                    className="form-control mb-3"
                                    placeholder="Enter the URL of an image (optional)"
                                />
                            </div>
                            <button
                                onClick={handleSubmit}
                                className="btn btn-dark w-100 mb-3"
                            >
                                Create Blog Post
                            </button>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Editor;
