import React from 'react';
import { updateUserRoleApi } from '../api/Api';

const UserTable = ({ users, isAdmin, updateUserList }) => {
    const handleRoleChange = (userId) => {
        // Implement logic to change user role
        updateUserRoleApi(userId)
            .then(response => {
                // Handle success response
                console.log('User role updated successfully:', response.data);
                // Update user list in parent component
                updateUserList();
            })
            .catch(error => {
                // Handle error response
                console.error('Error updating user role:', error);
            });
    };

    return (
        <table>
            <thead>
                <tr>
                    <th>First Name</th>
                    <th>Last Name</th>
                    <th>Email</th>
                    <th>Admin</th>
                    {isAdmin && <th>Action</th>} {/* Only show Action column if user is admin */}
                </tr>
            </thead>
            <tbody>
                {users.map(user => (
                    <tr key={user._id}>
                        <td>{user.firstName}</td>
                        <td>{user.lastName}</td>
                        <td>{user.email}</td>
                        <td>{user.isAdmin ? 'Yes' : 'No'}</td>
                        {isAdmin && ( // Show button only if user is admin
                            <td>
                                <button onClick={() => handleRoleChange(user._id)}>
                                    Change Role
                                </button>
                            </td>
                        )}
                    </tr>
                ))}
            </tbody>
        </table>
    );
};

export default UserTable;
