import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import { getSingleProductApi } from '../../api/Api'

const AdminEdit = () => {

    const { id } = useParams()

    useEffect(() => {
        getSingleProductApi(id).then((res) => {
            console.log(res.data)
            setProductName(res.data.product.productName)

        })
    }, [id])

    //make useState
    const [productName, setProductName] = useState('')
    const [productPrice, setProductPrice] = useState('')
    const [productCategory, setProductCategory] = useState('')
    const [productDescription, setProductDescription] = useState('')

    const [productImage, setProductImage] = useState(null)
    const [previewImage, setPreviewImage] = useState(null)

    const handleProductName = (e) => {
        setProductName(e.target.value)
    }
    const handleProductPrice = (e) => {
        setProductPrice(e.target.value)
    }
    const handleProductCategory = (e) => {
        setProductCategory(e.target.value)
    }
    const handleProductDescription = (e) => {
        setProductDescription(e.target.value)
    }

    const handleImageUpload = (e) => {
        const file = e.target.files[0]
        setProductImage(file)
        setPreviewImage(URL.createObjectURL(file))
    }

    return (
        <>
            <h2 className='m-4'>Updating product for <span className='text-danger'> 'iPhone 15'</span></h2>
            <div className='d-flex m-4 gap-4'>
                <div className=''>
                    <form>
                        <label>Product</label>
                        <input value={productName} onChange={handleProductName} type="text" className='form-control m-2' placeholder='Enter Product Name' />
                        <label>Price</label>
                        <input onChange={handleProductPrice} type="number" className='form-control m-2' placeholder='Enter Product Price' />
                        <label>Category</label>
                        <select onChange={handleProductCategory} className='form-control'>
                            <option value="Flower">Flower</option>
                            <option value="Cake">Cake</option>
                            <option value="Gift">Gift</option>

                        </select>
                        <label>Description</label>
                        <textarea onChange={handleProductDescription} type="text" className='form-control m-2' placeholder='Enter Product Description' />

                        <label>Image</label>
                        <input onChange={handleImageUpload} type="file" className='form-control m-2' placeholder='Enter Product Image' />
                        <button className="btn btn-primary w-100 m-2">Update Product</button>

                    </form>
                </div>
                <div>
                    <h6>Old Image</h6>
                    <img src="https://th.bing.com/th/id/OIP.wMzAuHDcivlTPKsZEJtA3QHaEo?rs=1&pid=ImgDetMain" alt="" className='object-fit-cover rounded-3' height={180} width={180} />
                    <hr />
                    {
                        previewImage && <>
                            <h6 className='mt-3'>New Image</h6>
                            <img src={previewImage} alt="" className='object-fit-cover rounded-3' height={180} width={180} />
                        </>
                    }
                </div>
            </div>
        </>
    )
}

export default AdminEdit
