import React, { useState } from "react";
import { toast } from "react-toastify";
import { createUserApi } from "../api/Api";

const Register = () => {
  // useState (setting input value)
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  //setting error message
  const [fnameError, setFnameError] = useState("");
  const [lnameError, setLnameError] = useState("");
  const [emailError, setEmailError] = useState("");
  const [passwordError, setPasswordError] = useState("");
  const [cPasswordError, setCPasswordError] = useState("");

  //function for validation
  const validate = () => {
    let isValid = true;
    if(firstName.trim() === ""){
      setFnameError("First name is required");
      isValid = false;
    }
    if(lastName.trim() === ""){
      setLnameError("Last name is required");
      isValid = false;
    }
    if(email.trim() ===""){

    }
  }

  //function to change input value
  const changeFirstName = (e) => {
    setFirstName(e.target.value);
  };
  const changeLastName = (e) => {
    setLastName(e.target.value);
  }
  const changeEmail = (e) => {
    setEmail(e.target.value);
  }
  const changePassword = (e) => {
    setPassword(e.target.value);
  }
  const changeConfirmPassword = (e) => {
    setConfirmPassword(e.target.value);
  }

  //function for button 
  const handleSubmit = (e) => {
    e.preventDefault()
    //check is the input value is available
    console.log(firstName, lastName, email, password);

    //check test api
    // testApi().then((res) => {
    //   console.log(res.data);
    // })

    //making json data object
    const data = {
      firstName: firstName,
      lastName: lastName,
      email: email,
      password: password
    }

    //makgin api call
    createUserApi(data).then((res) => {
      if (res.data.success === false) {
        toast.error(res.data.message)
      } else {
        toast.success(res.data.message)
      }
    }).catch((err) => {
      toast.error("Server Error")
      console.log(err.message)
    })
  };
  return (
    <>
      <h1 className="m-4">Create Your Account</h1>
      <form className="m-4 w-25">
        <label>First Name</label>
        <input
          onChange={changeFirstName}
          type="text"
          className="form-control mb-3"
          placeholder="Enter your first name"
        />
        <label>Last Name</label>
        <input
          onChange={changeLastName}
          type="text"
          className="form-control mb-3"
          placeholder="Enter your last name"
        />
        <label>Email</label>
        <input
          onChange={changeEmail}
          type="email"
          className="form-control mb-3"
          placeholder="Enter your email"
        />
        <label>Password</label>
        <input
          onChange={changePassword}
          type="password"
          className="form-control mb-3"
          placeholder="Enter your password"
        />
        <label>Confirm Password</label>
        <input
          onChange={changeConfirmPassword}
          type="password"
          className="form-control mb-3"
          placeholder="Enter your password"
        />
        <button onClick={handleSubmit} className="btn btn-danger w-100">Create an Account</button>
        <a href="" className="text-black text-decoration-none">Already have an account? Login</a>
      </form>
    </>
  );
};

export default Register;
