import React, { useEffect, useState } from 'react'
import { toast } from 'react-toastify'
import { createProductApi, getAllProductsApi } from '../../api/Api'
import { Link } from 'react-router-dom'

const AdminDashboard = () => {
    //make useState
    const [productName, setProductName] = useState('')
    const [productPrice, setProductPrice] = useState('')
    const [productCategory, setProductCategory] = useState('')
    const [productDescription, setProductDescription] = useState('')

    const [productImage, setProductImage] = useState(null)
    const [previewImage, setPreviewImage] = useState(null)

    // useEffect for fetching all products and show in table
    const [products, setProducts] = useState([])
    useEffect(() => {
        getAllProductsApi().then((res) => {
            setProducts(res.data.products)
        })
    }, [])

    const handleProductName = (e) => {
        setProductName(e.target.value)
    }
    const handleProductPrice = (e) => {
        setProductPrice(e.target.value)
    }
    const handleProductCategory = (e) => {
        setProductCategory(e.target.value)
    }
    const handleProductDescription = (e) => {
        setProductDescription(e.target.value)
    }
    const handleImageUpload = (e) => {
        const file = e.target.files[0]
        setProductImage(file)
        setPreviewImage(URL.createObjectURL(file))
    }

    const handleSubmit = (e) => {
        e.preventDefault()

        //making logical form data
        const formData = new FormData()
        formData.append('productName', productName)
        formData.append('productPrice', productPrice)
        formData.append('productCategory', productCategory)
        formData.append('productDescription', productDescription)
        formData.append('productImage', productImage)

        createProductApi(formData).then((res) => {
            if (res.data.successs) {
                toast.error(res.data.message)
            } else {
                toast.success(res.data.message)
            }
        }).catch(err => {
            toast.error("Server error");
            console.log(err.message);
        })
    }

    //delete product 
    const handleDelete = (id) => {
        const confirmDialog = window.confirm("Are you sure you want to delete this product?")


    }
    return (
        <div>
            <>
                <div className='m-4'>
                    <div className='d-flex justify-content-between'>
                        <h2>Admin Dashboard</h2>
                        <button type="button" className="btn btn-primary" data-bs-toggle="modal" data-bs-target="#exampleModal">
                            Add Product
                        </button>

                        <div className="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                            <div className="modal-dialog">
                                <div className="modal-content">
                                    <div className="modal-header">
                                        <h1 className="modal-title fs-5" id="exampleModalLabel">Add new Product</h1>
                                        <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                                    </div>
                                    <div className="modal-body">
                                        <form>
                                            <label>Product</label>
                                            <input onChange={handleProductName} type="text" className='form-control m-2' placeholder='Enter Product Name' />
                                            <label>Price</label>
                                            <input onChange={handleProductPrice} type="number" className='form-control m-2' placeholder='Enter Product Price' />
                                            <label>Category</label>
                                            <select onChange={handleProductCategory} className='form-control'>
                                                <option value="Flower">Flower</option>
                                                <option value="Cake">Cake</option>
                                                <option value="Gift">Gift</option>

                                            </select>
                                            <label>Description</label>
                                            <textarea onChange={handleProductDescription} type="text" className='form-control m-2' placeholder='Enter Product Description' />

                                            <label>Image</label>
                                            <input onChange={handleImageUpload} type="file" className='form-control m-2' placeholder='Enter Product Image' />
                                            {
                                                previewImage && <img src={previewImage} className='img-fluid rounded object-fit-cover' alt="Product image" />
                                            }
                                        </form>
                                    </div>
                                    <div className="modal-footer">
                                        <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                                        <button onClick={handleSubmit} type="button" className="btn btn-primary">Save changes</button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                    <table className="table mt-2">
                        <thead className='table-dark'>
                            <tr>
                                <th>Product Image</th>
                                <th>Product Name</th>
                                <th>Product Price</th>
                                <th>Product Category</th>
                                <th>Product Description</th>
                                <th>action</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                products.map((item) => (
                                    <tr>
                                        <td>
                                            <img src={item.productImageUrl} width={'40'} height={'40'} alt="" />
                                        </td>
                                        <td>{item.productName}</td>
                                        <td>NRP.{item.productPrice}</td>
                                        <td>{item.productCategory}</td>
                                        <td>{item.productDescription.slice(0, 20)}</td>
                                        <td>
                                            <div className='btn-group' role='group'>
                                                <Link to={`/admin/edit/${item._id}`} className='btn btn-success'>Edit</Link>
                                                <button onClick={() => handleDelete(item._id)} className='btn btn-danger '>Delete</button>
                                            </div>
                                        </td>
                                    </tr>
                                ))
                            }
                        </tbody>
                    </table>
                </div>
            </>
        </div>
    )
}

export default AdminDashboard
