import React, { useEffect, useState } from 'react';
import { getSingleBlogApi } from '../api/Api';

const SingleBlogPost = ({ blogId }) => {
    const [blog, setBlog] = useState(null);

    useEffect(() => {
        const fetchBlog = async () => {
            try {
                const response = await getSingleBlogApi(blogId);
                setBlog(response.data.blog);
            } catch (error) {
                console.error('Error fetching single blog post:', error);
            }
        };

        fetchBlog();
    }, [blogId]);

    if (!blog) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <div className="media my-4 d-flex align-items-start">
                <img
                    src={blog.blogImageUrl || 'https://via.placeholder.com/150'}
                    className="mr-3"
                    alt={blog.title}
                    style={{ width: '150px', height: '150px' }}
                />
                <div className="media-body flex-grow-1" style={{ marginLeft: '1rem' }}>
                    <h5 className="mt-0">{blog.title}</h5>
                    <p className="small text-muted mb-0">
                        <strong>Author:</strong>{' '}
                        {`${blog.user.firstName || 'Unknown'} ${blog.user.lastName || 'Unknown'}`}
                    </p>
                    <p>{blog.content}</p>
                </div>
            </div>
            <div>
                <h5>Comments</h5>
                {blog.comments.map(comment => (
                    <div key={comment._id} className="mb-3">
                        <p>{comment.content}</p>
                        {comment.children && comment.children.length > 0 && (
                            <div className="ms-4">
                                {comment.children.map(child => (
                                    <div key={child._id} className="mb-2">
                                        <p>{child.content}</p>
                                    </div>
                                ))}
                            </div>
                        )}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default SingleBlogPost;
