import React, { useState } from 'react'
import { createUserApi, testApi } from '../apis/Api'
import { toast } from 'react-toastify'

const Register = () => {
  // useState (Setting input value)
  const [firstName, setFirstName] = useState('')
  const [lastName, setLastName] = useState('')
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [confirmPassword, setConfirmPassword] = useState('')
  const [loading, setLoading] = useState(false)
  // useState (Setting error message)
  const [fnameError, setFnameError] = useState('')
  const [lnameError, setLnameError] = useState('')
  const [emailError, setEmailError] = useState('')
  const [passwordError, setPasswordError] = useState('')
  const [cpasswordError, setCpasswordError] = useState('')

  // validate input value
  const validate = () => {
    let isValid = true

    // reset error message
    setFnameError('')
    setLnameError('')
    setEmailError('')
    setPasswordError('')
    setCpasswordError('')

    if (firstName.trim() === "") {
      setFnameError("Firstname is required")
      isValid = false
    }
    if (lastName.trim() === "") {
      setLnameError("Lastname is required")
      isValid = false
    }
    if (email.trim() === "") {
      setEmailError("Email is required")
      isValid = false
    }

    if (password.trim() === "") {
      setPasswordError("Password is required")
      isValid = false
    }

    if (confirmPassword.trim() === "") {
      setCpasswordError("Confirm Password is required")
      isValid = false
    }

    if (password.trim() !== confirmPassword.trim()) {
      setCpasswordError("Password and Confirm Password must be same")
      isValid = false
    }

    return isValid
  }

  // function for changing input value
  const changeFirstname = (e) => {
    setFirstName(e.target.value)
  }

  const changeLastname = (e) => {
    setLastName(e.target.value)
  }

  const changeEmail = (e) => {
    setEmail(e.target.value)
  }

  const changePassword = (e) => {
    setPassword(e.target.value)
  }

  const changeConfirmPassword = (e) => {
    setConfirmPassword(e.target.value)
  }

  // function for button
  const handleSubmit = (e) => {
    e.preventDefault()

    // validate the data
    const isValid = validate()
    if (!isValid) {
      return
    }


    // making json data object
    const data = {
      firstName: firstName,
      lastName: lastName,
      email: email,
      password: password
    }

    // making API Call
    createUserApi(data).then((res) => {
      if (res.data.success == false) {
        toast.error(res.data.message)
      } else {
        toast.success(res.data.message)
      }
    }).catch(err => {
      toast.error("Server Error")
      console.log(err.message)
    })



  }

  return (
    <div style={styles.container}>
      <div style={styles.leftContainer}>
        {/* Add your image here */}
        <img
          src="https://media.istockphoto.com/id/692557700/photo/burger.webp?b=1&s=170667a&w=0&k=20&c=t6SrUepw29BNHzYJCDuLKFdC7XQuL0r_-UBuZwA-WWk="
          alt="Registration"
          style={styles.image}
        />
      </div>
      <div style={styles.rightContainer}>
        <div style={styles.registerBox}>
          <h2>    Create a New Account    </h2>
          <form className="m-4">
            <label style={styles.label}>Firstname</label>
            <input
              onChange={changeFirstname}
              type="text"
              className="form-control mb-2"
              placeholder="Enter your first name"
            />
            {
              fnameError && <p className='text-danger'>{fnameError}</p>
            }
            <label style={styles.label}>Lastname</label>
            <input
              onChange={changeLastname}
              type="text"
              className="form-control mb-2"
              placeholder="Enter your last name"
            />
            {
              lnameError && <p className='text-danger'>{lnameError}</p>
            }
            <label style={styles.label}>Email</label>
            <input
              onChange={changeEmail}
              type="email"
              className="form-control mb-2"
              placeholder="Enter your Email"
            />
            {
              emailError && <p className='text-danger'>{emailError}</p>
            }
            <label style={styles.label}>Password</label>
            <input
              onChange={changePassword}
              type="password"
              className="form-control mb-2"
              placeholder="Enter your Password"
            />
            {
              passwordError && <p className='text-danger'>{passwordError}</p>
            }
            <label style ={styles.label}>Confirm Password</label>
            <input onChange={changeConfirmPassword} type="password" className='form-control mb-2' placeholder='Enter your password' />
            {
              cpasswordError && <p className='text-danger'>{cpasswordError}</p>
            }

            <button
              className="btn btn-warning w-100"
              onClick={handleSubmit}
              disabled={loading}
            >
              {loading ? 'Creating Account...' : 'Create an Account'}
            </button>
            <p className="mt-2">
              Already have an account?{' '}
              <a href="/login" className="text-black text-decoration-none">
                Login
              </a>
            </p>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Register

const styles = {
  container: {
    display: 'flex',
    height: '100vh',
  },
  leftContainer: {
    flex: 1,
    overflow: 'hidden',
    position: 'relative',
  },
  rightContainer: {
    flex: 1,
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  image: {
    width: '100%',
    height: '100%',
    objectFit: 'cover',
    position: 'absolute',
  },
  registerBox: {
    padding: '10px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    borderRadius: '8px',
    background: '#ffffff',
    textAlign: 'center',
    maxWidth: '400px', // Adjust as needed
  },
  label: {
    textAlign: 'left',
    display: 'block',
    marginBottom: '4px',
  },

};
